let storyState = new StoryState();
GlobalState.storyState = storyState;
let IDMap = new Map();
let index = 0;

export function activate(context: vscode.ExtensionContext) {
    const provider = new StoryDefinitionProvider();
    const ds = new StorySymbolsProvider();

    for (;;) {

        if (fs.existsSync(`./record${index}`)) {
            let temp = new StoryMission(`record${index}`);
            storyState.missions.set(index, temp);

            let variablesPath = `./record${index}Variable`;
            let varId = 0;
            for (;;) {
                if (fs.existsSync(`${variablesPath}${varId}`)) {
                    let varTemp = new SamiConstVariable(`${variablesPath}${varId}`);
                    storyState.variables.set([index, varId], varTemp );
                    varId++;
                } else {
                    break;
                }
            }
            for (var i = 0; i < varId; i++){
                let _temp = storyState.missions.get(index)!.getActionByID(i);

                IDMap.set(storyState.variables.get([index, i]), _temp);
            }
        } else {
            break;
        }
        index++;
    }
    // Use the console to output diagnostic information (console.log) and errors (console.error)
    console.log("YakLib extension activated.");

    // create a combined completion item provider for all file types
    const completionProvider = new CompletionProvider();
    const completionProviderFiles = ["javascript", { language: "json", scheme: "untitled" }];

    const triggerChars = [".", "'", '"', ":", " ", "-"];
    for (let i = 0; i < triggerChars.length; i++) {
        const triggerChar = triggerChars[i];
        context.subscriptions.push(
            vscode.languages.registerCompletionItemProvider(
                completionProviderFiles,
                completionProvider,
                triggerChar
            )
        );
    }
} 

class StoryDefinitionProvider implements vscode.DefinitionProvider {
    async provideDefinition(document: vscode.TextDocument, position: vscode.Position, token: vscode.CancellationToken): Promise<vscode.Definition> {
        const range = document.getWordRangeAtPosition(position);
        let rangeWithId: vscode.Range | null = document.getWordRangeAtPosition(position, /[^(){};=\s\t\.,]+/g);
        const prefixData: any = await completionProvider.getCompleteTxt(document.getText(), rangeWithId) || [];
        const c = prefixData.cmb;
        const completion = IDMap.get(prefixData.cmb);
        if (!completion) {
            return {
                range
            };
        }
        //if (range == null) range = new vscode.Range(position, position);
        return [completion.location];
    }
}

