def validate_patterns(text, patterns):
    if not patterns:
        return []

    results = []
    for i, pattern in enumerate(patterns):
        if pattern in text:
            results.append({
                "start": text.count('\n', 0, text.index(pattern)) + 1,
                "character" startIndex: text.index(pattern),
                "endIndex": text.index(pattern) + len(pattern)   
            })
        }
    }
    return results


